import "./ExpensesForm.css";

function ExpensesForm() {

const [enteredTitle, setEnteredTitle] = useState(null)
const [enteredAmount, setEnteredAmount] = useState(null)
const [enteredDate, setEnteredDate] = useState(null)


const titleChangeHandler = (e) => {
let input = e.target.value
setEnteredTitle(input)
console.log(input);
}


const amountChangeHandler = (e) => {
    let input = e.target.value
    setEnteredAmount(input)
    console.log(input);
    }


    const dateChangeHandler = (e) => {
        let input = e.target.value
        setEnteredDate(input)
        console.log(input);
        }


  return (
    <form>
      <div className="new-expense__controls">
        <div className="new-expense__control">
          <label>Title</label>
          <input type="text" onChange={titleChangeHandler}/>
        </div>
      </div>

      <div className="new-expense__controls">
        <div className="new-expense__control">
          <label>Amount</label>
          <input type="number" min="0.01" step="0.01" onChange={amountChangeHandler}/>
        </div>
      </div>

      <div className="new-expense__controls">
        <div className="new-expense__control">
          <label>Date</label>
          <input type="date" min="2021-01-01" max="2024-01-01" onChange={dateChangeHandler}/>
        </div>
      </div>

<div className="new-expense__actions">
<button type="submit">Add Expense</button>


</div>

    </form>
  );
}

export default ExpensesForm;
