import ExpenseItem from "./ExpenseItem";
import "./Expenses.css";
import ExpensesFilter from "./ExpensesFilter";
import React, { useState } from "react";

/*Props passed from app.js */
function Expenses(props) {
  //selected year in the filter dropdown
  const [selectedYear, setSelectedYear] = useState(2020);


  //filtered list.
  const filteredExpenses = props.list.filter((ele) => 
    (ele.date.getYear() + 1900) === Number(selectedYear));


  function filterHandler(e) {
    let pickedYear = e.target.value;
    setSelectedYear(pickedYear);
  }

  return (
    <div className="expenses">
      <ExpensesFilter
        year={props.year}
        selectedYear={props.selectedYear}
        filterhandler={filterHandler}
      />




      {
      filteredExpenses.length === 0 ? (<p>No Expenses found.</p>)
      : 
      (
      filteredExpenses.map((ele) => {
        return (
          <ExpenseItem
            key={ele.id}
            date={ele.date}
            title={ele.title}
            amount={ele.amount}
          />
        );
      })
      )

      }
    </div>
  );
}

export default Expenses;
