import Expenses from "./components/ExpenseSection/Expenses";
import NewExpenses from "./components/NewExpenses/NewExpenses";
import React, { useState } from "react";


const INITIALexpenses = [
  {
    id: "e1",
    title: "Toilet Paper",
    amount: 94.12,
    date: new Date(2020, 7, 14),
  },
  { id: "e2", title: "New TV", amount: 799.49, date: new Date(2021, 2, 12) },
  {
    id: "e3",
    title: "Car Insurance",
    amount: 294.67,
    date: new Date(2021, 2, 28),
  },
  {
    id: "e4",
    title: "New Desk (Wooden)",
    amount: 450,
    date: new Date(2021, 5, 12),
  },
];



function App() {
  const [thisDataYear, setDataYear] = useState(null);

     //selected year in the filter dropdown
     const [selectedYear, setSelectedYear] = useState(2020);

  const [expenses, setExpenses] = useState(INITIALexpenses);

  const [filteredExpenses, setFilteredExpenses] = useState(INITIALexpenses);


  const fetchExpenseData = (data) => {
    let dataCopy = {
      ...data,
      id: Math.random().toString(),
    };


setExpenses(prevExpenses => {return [dataCopy, ...prevExpenses]});
setFilteredExpenses(expenses)


    //get date object
    let date = dataCopy.date;

    //year is of type number
    let year = date.getFullYear();

    setDataYear(year);
  };





  function filterHandler(e) {
    let pickedYear = e.target.value;
    setSelectedYear(pickedYear);
    let filteredList = expenses.filter(ele => 
       { 
        return (
           (ele.date.getYear() + 1900) === Number(pickedYear)
           )

    })

    setFilteredExpenses(filteredList);
    console.log(filteredExpenses)


    }



  return (
    <div>
      <NewExpenses saveExpenseData={fetchExpenseData} />
      <Expenses selectedYear={selectedYear} year={thisDataYear} list={expenses} filteredList={filteredExpenses} filterHandler={filterHandler} />
    </div>
  );
}

export default App;
