import ExpenseItem from "./ExpenseItem";
import './Expenses.css';
import ExpensesFilter from "./ExpensesFilter";
import React, {useState} from "react";

/*Props passed from app.js */
function Expenses(props) {


    //selected year in the filter dropdown
    const [selectedYear, setSelectedYear] = useState("2020");


    function filterHandler(e) {
        let pickedYear = e.target.value;
        setSelectedYear(pickedYear);
        }


    return(
    <div className="expenses">


<ExpensesFilter year={props.year} selectedYear={selectedYear} filterhandler={filterHandler}/>


 {props.list.map((ele, ind) => { return (
        <ExpenseItem key={ind} date={ele.date} title={ele.title} amount={ele.amount}/>
        ) }
        )}

        </div>)

}


export default Expenses
